DROP TABLE IF EXISTS gdm_duty_region CASCADE;

CREATE TABLE gdm_duty_region (
    id numeric(16,0),
    code character varying(50) NOT NULL,
    name character varying(150) NOT NULL,
    type numeric(2,0) NOT NULL,
    status numeric(2,0) NOT NULL,
    org_id character varying(50) NOT NULL,
    lon numeric(16,6) NOT NULL,
    lat numeric(16,6) NOT NULL,
    address character varying(500) NOT NULL,
    memo character varying(4000) NOT NULL,
    region_content text NOT NULL,
    important_point text NOT NULL,
    creator character varying(50) NOT NULL,
    fillcolor character varying(50) NOT NULL,
    isused numeric(1,0) NOT NULL,
    isdeleted numeric(1,0) NOT NULL,
    zrld character varying(150) NOT NULL,
    lxdh character varying(150) NOT NULL,
    dwdh character varying(150) NOT NULL,
    orgname character varying(50) NOT NULL,
    orgid_l1 character varying(50) NOT NULL,
    orgid_l2 character varying(50) NOT NULL,
    create_time timestamp(6) without time zone NOT NULL,
    update_time timestamp(6) without time zone NOT NULL,
    geom geometry NOT NULL
);


CREATE UNIQUE INDEX pk_ds_duty_region ON gdm.gdm_duty_region USING btree (id);

